window.onbeforeunload = function() {
  window.scrollTo(0, 0);
}

var swiper = new Swiper('.sbl_swiperCont', {
    effect: 'fade',
    loop: true,
    autoplay: {
      delay: 5000,
      disableOnInteraction: false,
    },on: {
        slideChangeTransitionEnd: function() {
          // Reset opacity to 0 for all slides
          var slides = document.querySelectorAll('.sf');
          slides.forEach(function(slide) {
            slide.style.opacity = 0;
          });
          // Set opacity to 1 for active slide
          var activeSlide = document.querySelector('.swiper-slide-active');
          activeSlide.style.opacity = 1;
        }
      }
  });

var swiper2 = new Swiper(".packSwipe", {
  slidesPerView: "auto",
  spaceBetween: 30,
});

var swiper3 = new Swiper(".mobileSwipe", {
  grabCursor: true,
  effect: "creative",
  creativeEffect: {
    prev: {
      shadow: true,
      translate: [0, 0, -5000],
    },
    next: {
      translate: ["100%", 0, 0],
    },
  },
});

document.querySelector('.btn-prev button').addEventListener('click', function() {
  swiper3.slidePrev();
});

document.querySelector('.btn-next button').addEventListener('click', function() {
  swiper3.slideNext();
  document.querySelector('.sbl_teamBoxM').style.opacity="1";
});

document.addEventListener('DOMContentLoaded', function () {
  const locoScroll = new LocomotiveScroll({
    el: document.querySelector('[data-scroll-container]'),
    smooth: true,
    smartphone: {
      smooth: false,
    },
    tablet: {
      smooth: true,
    },
  });
  locoScroll.on('scroll', (instance) => {
    const nav = document.querySelector('.sec_Nav');

    if (instance.scroll.y > 0 ) {
        nav.style.backgroundColor = 'rgb(0, 0, 0, 0.8)';
    } else if (instance.scroll.y <= 0 ) {
        nav.style.backgroundColor = 'rgba(255, 255, 255, 0)';
    }
});
});

document.addEventListener('DOMContentLoaded', function() {
  var loadingOverlay = document.getElementById('loading-overlay');

  window.onload = function() {
      
      loadingOverlay.style.transition = '0.5s ease';
      loadingOverlay.style.transform = 'translateY(-100%)'; 
  };
});